#include <iostream>#include <vector>using namespace std;int main() {  int n, nc;  vector<int> snacks;  cin >> n;  nc = n;  while (nc > 0) {    int snack;    cin >> snack;    snacks.push_back(snack);    --nc;  }    string answer = "";    int pindex = 0;  int pmax = snacks[pindex];    for (int i = pindex + 1; i < snacks.size(); ++i) {    if (snacks[i] > pmax) {      pmax = snacks[i];      pindex = i;    }  }    int before = pindex;  int nmax, nindex;    while (pindex < n - 1) {    nindex = pindex + 1;    nmax = snacks[pindex + 1];    for (int i = nindex + 1; i < snacks.size(); ++i) {      if (snacks[i] > nmax) {        nmax = snacks[i];        nindex = i;      }    }        for (int i = 0; i < before; ++i) {      answer += "\n";    }        answer += to_string(pmax);    for (int i = pmax - 1; i > nmax; --i) {      answer += " " + to_string(i);    }        before = nindex - pindex;    pindex = nindex;    pmax = nmax;  }    for (int i = 0; i < before; ++i) {    answer += "\n";  }    answer += to_string(pmax);  for (int i = pmax - 1; i > 0; --i) {    answer += " " + to_string(i);  }    cout << answer << endl;    return 0;}